import React from "react";
import { Component } from "react";

const MAX_RECORDS_PER_PAGE = 4;
class Tasks extends Component {
    state = {
        currentPage: 1
    };

    handleClick = number => event => {
        this.setState({ currentPage: number });
    };

    getFilteredTasks() {
        switch (this.props.view) {
            case "COMPLETE":
                return this.props.tasks.filter(v => v.status === "COMPLETE");
            case "INCOMPLETE":
                return this.props.tasks.filter(v => v.status === "INCOMPLETE");
            default:
                return this.props.tasks;
        }
    }
    getTasksForPage(pagno) {
        const indexOfLastTask = this.state.currentPage * maxRecordsPerPage;
        const indexOfFirstTask = indexOfLastTask - maxRecordsPerPage;
        return this.getFilteredTasks().slice(indexOfFirstTask, indexOfLastTask);
    }
    render() {
        const totalTasks = this.getFilteredTasks().length;
        const maxRecordsPerPage = MAX_RECORDS_PER_PAGE;

        const filteredTasks = this.getFilteredTasks();
        const currentTasks = this.getTasksForPage(this.state.currentPage);

        const renderTasks = (
            <ul className="tasklist" id="todoList">
                {currentTasks.map(task => (
                    <li
                        key={task.id}
                        className={
                            task.status === "INCOMPLETE"
                                ? "task incomplete"
                                : "task complete"
                        }
                        onClick={() => this.props.toggleTask(task)}
                    >
                        <div className="todoContainer">{task.name}</div>

                        <button
                            className="delete"
                            onClick={ev => this.props.onDelete(task.id, ev)}
                        >
                            X
                        </button>
                    </li>
                ))}
            </ul>
        );

        const pageNumbers = [];

        for (let i = 1; i <= Math.ceil(totalTasks / maxRecordsPerPage); i++) {
            console.log(i);
            pageNumbers.push(i);
        }
        const renderPages = (
            <ul className="page-numbers">
                <li
                    className={
                        this.state.currentPage === 1
                            ? "disabled pages"
                            : "pages"
                    }
                    onClick={ev => {
                        console.log(ev.target.className);
                        ev.target.className !== "disabled pages" &&
                            this.setState({
                                currentPage: this.state.currentPage - 1
                            });
                    }}
                >
                    Prev
                </li>
                {pageNumbers.map(number => (
                    <li
                        className="pages"
                        key={number}
                        id={number}
                        onClick={this.handleClick(number)}
                    >
                        {number}
                    </li>
                ))}
                <li
                    className="pages"
                    onClick={() => {
                        this.setState({
                            currentPage: this.state.currentPage + 1
                        });
                        console.log(this.state.currentPage);
                    }}
                >
                    Next
                </li>
            </ul>
        );

        return (
            <React.Fragment>
                {renderTasks}
                {renderPages}
            </React.Fragment>
        );
    }
}

export default Tasks;
