import React from "react";
import { Component } from "react";

class Tasks extends Component {
    state = {
        displayTasks: (this.props.view === "ALL" && [...this.props.tasks]) ||
            (this.props.view === "COMPLETE" && [
                ...this.props.tasks.filter(task => task.status === "COMPLETE")
            ]) || [
                ...(this.state.displayTasks = this.props.tasks.filter(
                    task => task.status === "INCOMPLETE"
                ))
            ],
        currentPage: 1,
        tasksPerPage: 4
    };
    constructor() {
        super();
        this.state = {};
        this.handleClick = this.handleClick.bind(this);
    }

    /* componentWillMount() {
        if (this.props.view === "ALL") {
            this.state.displayTasks = [...this.props.tasks];
        } else if (this.props.view === "COMPLETE") {
            this.state.displayTasks = this.props.tasks.filter(
                task => task.status === "COMPLETE"
            );
        } else {
            this.state.displayTasks = this.props.tasks.filter(
                task => task.status === "INCOMPLETE"
            );
        }
    } */

    handleClick = event => {
        this.setState({ currentPage: event.target.id });
    };
    render() {
        const indexOfLastTask =
            this.state.currentPage * this.state.tasksPerPage;
        const indexOfFirstTask = indexOfLastTask - this.state.tasksPerPage;
        const currentTasks = this.state.displayTasks.slice(
            indexOfFirstTask,
            indexOfLastTask
        );

        const renderTasks = (
            <ul className="tasklist" id="todoList">
                {currentTasks.map(task => (
                    <li
                        key={task.id}
                        className={
                            task.status === "INCOMPLETE"
                                ? "task incomplete"
                                : "task complete"
                        }
                        onClick={() => this.props.toggleTask(task)}
                    >
                        <div className="todoContainer">{task.name}</div>

                        <button
                            className="delete"
                            onClick={ev => this.props.onDelete(task.id, ev)}
                        >
                            X
                        </button>
                    </li>
                ))}
            </ul>
        );

        const pageNumbers = [];
        for (
            let i = 1;
            i <=
            Math.ceil(this.state.displayTasks.length / this.state.tasksPerPage);
            i++
        ) {
            pageNumbers.push(i);
        }
        const renderPages = (
            <ul id="page-numbers">
                {pageNumbers.map(number => (
                    <li key={number} id={number} onClick={this.handleClick}>
                        {number}
                    </li>
                ))}
            </ul>
        );

        return (
            <React.Fragment>
                {renderTasks}
                {renderPages}
            </React.Fragment>
        );
    }
}

export default Tasks;
