import React from "react";
import { Component } from "react";

class Tasks extends Component {
    constructor() {
        super();
        this.state = {
            displayTasks: [],
            currentPage: 1,
            tasksPerPage: 4
        };
        this.handleClick = this.handleClick.bind(this);
        if (this.props.view === "ALL") {
            displayTasks = [...this.props.tasks];
        } else if (this.props.view === "COMPLETE") {
            displayTasks = this.props.tasks.filter(
                task => task.status === "COMPLETE"
            );
        } else {
            displayTasks = this.props.tasks.filter(
                task => task.status === "INCOMPLETE"
            );
        }
    }

    handleClick = event => {
        this.setStatse({ currentPage: event.target.id });
    };
    render() {
        const { displayTasks, currentPage, tasksPerPage } = this.state;
        const indexOfLastTask = currentPage * tasksPerPage;
        const indexOfFirstTask = indexOfLastTask - tasksPerPage;
        const currentTasks = displayTasks.slice(
            indexOfFirstTask,
            indexOfLastTask
        );

        const renderTasks = (
            <ul className="tasklist" id="todoList">
                {currentTasks.map(task => (
                    <li
                        className="task"
                        key={task.id}
                        className={
                            task.status === "INCOMPLETE"
                                ? "task incomplete"
                                : "task complete"
                        }
                        onClick={() => props.toggleTask(task)}
                    >
                        <div className="todoContainer">{task.name}</div>

                        <button
                            className="delete"
                            onClick={ev => props.onDelete(task.id, ev)}
                        >
                            X
                        </button>
                    </li>
                ))}
            </ul>
        );

        return (
            <React.Fragment>
                <ul className="tasklist" id="todoList">
                    {displayTasks.map(task => (
                        <li
                            className="task"
                            key={task.id}
                            className={
                                task.status === "INCOMPLETE"
                                    ? "task incomplete"
                                    : "task complete"
                            }
                            onClick={() => props.toggleTask(task)}
                        >
                            <div className="todoContainer">{task.name}</div>

                            <button
                                className="delete"
                                onClick={ev => props.onDelete(task.id, ev)}
                            >
                                X
                            </button>
                        </li>
                    ))}
                </ul>
            </React.Fragment>
        );
    }
}

export default Tasks;
